mapper.select:
SELECT C.item_name , R.alarm_type , R.create_time,R.project_name,R.sensor_number,R.smu_number,R.phone,R.email,R.smu_channel,R.user_name,R.project_id,R.alarm_context FROM sys_code C,( SELECT S.monitor_type ,T.* FROM sensor S,( SELECT A.alarm_context,A.alarm_type,A.create_time,A.project_name,A.sensor_number,A.smu_number ,M.phone,M.email,M.user_name ,A.project_id,A.smu_channel FROM alarm A LEFT JOIN alarm_linkman M ON A.project_id=M.project_id ) T
WHERE S.sensor_number = T.sensor_number AND S.smu_number = T.smu_number AND S.project_id = T.project_id AND S.smu_channel = T.smu_channel AND ((unix_timestamp(NOW())-unix_timestamp(T.create_time))/60) <=10) R
WHERE R.monitor_type = C.sc_id;
